{
    "live_job_id":"PUT-LIVE-JOB-ID-HERE",
    "outputs":[
        {
            "label": "60 secs by stream from min 2 to min 3",
            "stream_start_time": 120,
            "stream_end_time": 180,
            "url": "ftp://yourftpserver.com/live/test_stream_min2to3.mp4",
            "credentials": "YOUR_CREDENTIALS"
        }
    ]
}

 // When using a transmuxed rendition within a multi-bitrate HLS output,
 // the segment_size and keyframe_interval should be avoided on any of the outputs
 // to ensure segments and keyframes follow the input GOP structure.
 //If not, the system will return an error in the job creation request.
{
    "live_stream": true,
    "region": "my-region-list,
    "reconnect_time": 20,
    "outputs": [
        {
            "label": "hls1080p transmux",
            "live_stream": true,
            "copy_video": true,
            "copy_audio": true,
            "segment_seconds": 6
        },
        {
            "label": "hls720p",
            "live_stream": true,
            "width": 960,
            "height": 540,
            "video_codec": "h264",
            "h264_profile": "main",
            "video_bitrate": 1843,
            "segment_seconds": 6
        },
        {
            "label": "hls480p",
            "live_stream": true,
            "width": 640,
            "height": 360,
            "video_codec": "h264",
            "h264_profile": "main",
            "video_bitrate": 819,
            "segment_seconds": 6
        },
        {
            "url":"s3://YOUR_BUCKET/live/20160403004644_test.mp4",
            "credentials": "YOUR_CREDENTIALS"
        }
    ]
}

 // When using your own FTP or S3 origin location,
 // your CDN must be configured to fallback to your origin location.
 // The Brightcove Live system will not validate that the origin locations
 // for the CDN’s provided in the job request.
{
    "live_stream": true,
    "region": "my-region-list",
    "reconnect_time": 30,
    "add_cdns":[
        {
            "label": "akamai",
            "prepend": "akamai.playback.com/someApplication",
            "protocol": "http"
        },
        {
            "label": "level3_1",
            "prepend": "l3.playback.io/somPath/someApplication/someFolder",
            "protocol": "http"
        }
    ],
    "outputs": [
        {
            "label": "hls1",
            "live_stream": true,
            "width": 960,
            "height": 540,
            "video_codec": "h264",
            "h264_profile": "main",
            "video_bitrate": 1843,
            "segment_seconds": 6,
            "keyframe_interval": 60
        },
        {
            "label": "hls2",
            "live_stream": true,
            "width": 640,
            "height": 360,
            "video_codec": "h264",
            "h264_profile": "main",
            "video_bitrate": 819,
            "segment_seconds": 6,
            "keyframe_interval": 60
        }
    ]
}

