// dedupe simple array
    function dedupe(arr) {
        var i,
            len = arr.length,
            out = [],
            obj = {};

        for (i = 0; i < len; i++) {
            obj[arr[i]] = 0;
        }
        for (i in obj) {
            out.push(i);
        }
        return out;
    };
    /*
     * combine and dedupe multiple items arrays
     * arguments: arr (an array of the objects
     * containing the items arrays you want to combine)
     * dependencies: dedupe()
     */
   function combineArrays(arr) {
        var i,
            iMax = arr.length,
            out = {};
        out.items = [];
        for (i = 0; i < iMax; i++) {
            out.items = out.items.concat(arr[i].items);
        }
        out.items = dedupe(out.items);
        return out;
    };
