/**
 * dedupe a simple array of strings or numbers
 * @param {array} arr the array to be deduped
 * @return {array} out the deduped array
 */
dedupe = function (arr) {
    var i,
        len = arr.length,
        out = [],
        obj = {};

    for (i = 0;i < len; i++) {
        obj[arr[i]] = 0;
    }
    for (i in obj) {
        out.push(i);
    }
    return out;
}
