/**
 * determines whether specified item is in an array
 *
 * @param {array} array to check
 * @param {string} item to check for
 * @return {boolean} true if item is in the array, else false
 */
function isItemInArray(arr, item) {
    var i,
        iMax = arr.length;
    for (i = 0; i < iMax; i++) {
        if (arr[i] === item) {
            return true;
        }
    }
    return false;
};
