/**
 * find a subset of objects in array of objects
 * based on some property value
 *
 * @param {array} targetArray - array to search
 * @param {string} objProperty - object property to search
 * @param {string|number} value - value of the property to search for
 * @return {array} array of objects with matching property value
*/
function getSubArray(targetArray, objProperty, value) {
    var i, totalItems = targetArray.length, objFound = false, idxArr = [];
    for (i = 0; i < totalItems; i++) {
        if (targetArray[i][objProperty] === value) {
            objFound = true;
            idxArr.push(targetArray[i]);
        }
    }
    return idxArr;
}
