// return ISO 8601 date string (YYYY-MM-DD) for JS date
function dateToISO(date) {
    var y = date.getFullYear(),
        m = date.getMonth(),
        d = date.getDate(),
        isoDate;
    y = y.toString();
    m = m + 1;
    if (m < 10) {
        m = "0" + m.toString();
    } else {
        m = m.toString();
    }
    if (d < 10) {
        d = "0" + d.toString();
    } else {
        d = d.toString();
    }
    isoDate = y + "-" + m + "-" + d;
    return isoDate;
};
